import { Validator } from "./validator";

export const groupArrayElements = (input, size) => {
  if (!Array.isArray(input)) {
    throw new Error(
      `ArgumentError: Expected \`input\` to be of type \`array\` but received ${typeOf(input)}`
    );
  }

  if(input.length <=0){
    throw new Error( "Please provide valid Array");
  }

  if (typeof size !== "number" || size === 0) {
    throw new Error("Please provide valid subarray number greater than 0");
  }

  if (input.length < size) {
    throw new Error(`This array cannot be divide in ${size} subarrays`);
  }
  // check if the data is valid or not
  const isValidationSuccessful = Validator(input, size);

  if (isValidationSuccessful !== "Success") {
    return isValidationSuccessful;
  }


  var noOfElementInOneArray = Math.ceil(input.length / size);
  let newArray = [];
  let index = 0;
  while (index < input.length) {
    newArray.push(input.slice(index, index + noOfElementInOneArray));
    index = index + noOfElementInOneArray;
  }
  return newArray;
};
