export const chunkedArrayFunction = (arr, size) => {
  var chunked = [];
  if(arr.length === size){
    return "cannot"
  }
  for (let element of arr) {

    const last = chunked[chunked.length - 1];
    if (!last || last.length === size) {
      chunked.push([element]);
    } else {
      last.push(element);
    }
  }
  return  chunked ;
};
