import { Validator } from "./validator";

export const groupArrayElements = (input, size) => {
  if (!Array.isArray(input)) {
    throw new Error(
      `ArgumentError: Expected \`input\` to be of type \`array\` but received ${typeOf(array)}`
    );
  }
  // check if the data is valid or not
  const isValidationSuccessful = Validator(array, size);

  if (isValidationSuccessful !== "Success") {
    return isValidationSuccessful;
  }

  var noOfElementInOneArray = Math.ceil(array.length / size);
  let newArray = [];
  let index = 0;
  while (index < array.length) {
    newArray.push(array.slice(index, index + noOfElementInOneArray));
    index = index + noOfElementInOneArray;
  }
  return newArray;
};
