export const Validator = (array, numberOfSubArray) => {
  if (typeof(numberOfSubArray)  !== "number" || numberOfSubArray === 0) {
    return {
      status: false,
      message: "Please provide valid subarray number greater than 0",
    };
  } else if (array.length < numberOfSubArray) {
    return {
      status: false,
      message: `This array cannot be divide in ${numberOfSubArray} subarrays`,
    };
  } else {
    return {
      status: true,
    };
  }
};
