export const Validator = (array, numberOfSubArray) => {
  if(typeof(numberOfSubArray) !== 'number'){
    return "Please provide valid subarray number"
  }else if(array.length<numberOfSubArray){
    return `This array cannot be divide in ${numberOfSubArray} subarrays`
  }else{
    return true
  }
};
